sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();

	// Query the database for NeedIt Task records with Due date field values older
	// then the current time.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete.
	var overdueNITasks3 = new GlideRecord('x_58872_needit_needit_task');
	overdueNITasks3.addQuery('due_date','<=',rightNow);
    overdueNITasks3.addQuery('due_date','<',gs.hoursAgo(-24));
	overdueNITasks3.addQuery('state','!=',3);

	overdueNITasks3.query();
	// Write a log message for each overdue NeedIt Task Record
	while(overdueNITasks3.next()){
		gs.info("Overdue NeedIt Task record = " + overdueNITasks3.number);	 gs.eventQueue('x_58872_needit.overdueNeedItTask3',overdueNITasks3,overdueNITasks3.number,gs.getUserName());
	}
